import { useState } from "react";
import WindowTracker from "./WindowTracker";

const Task4 = () => {
  const [show, setShow] = useState(true);
  const toogleWinTracker = () => setShow((prevShow) => !prevShow);
  return (
    <div className="task">
      <button onClick={toogleWinTracker}>Toogle Win Tracker</button>
      {show && <WindowTracker />}
    </div>
  );
};

export default Task4;
