import { useState } from "react";

const Task2 = () => {
  const list = ["item 0"];
  // list.push("emili");
  const [item, setItem] = useState(list);
  const addItem = () => {
    // setItem([...item, "emili " + new Date().toISOString()]);
    setItem((prevItem) => {
      return [...prevItem, "item " + (prevItem.length + 1)];
    });
  };
  return (
    <div>
      <button onClick={addItem}>Add</button>
      <ul>
        {item.map((res) => (
          <li key={res}>${res}</li>
        ))}
      </ul>
    </div>
  );
};

export default Task2;
