import {
  Box,
  Container,
  FormControl,
  Grid,
  InputLabel,
  MenuItem,
  Select,
} from "@mui/material";
import React from "react";
import Announcement from "../components/Announcement";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";
import Newsetter from "../components/Newsetter";
import Products from "../components/Products";

const ProductList = () => {
  const [age, setAge] = React.useState("");
  const [sortBy, setSortBy] = React.useState("");

  const handleChange = (event) => {
    setAge(event.target.value);
  };
  const handleSortBy = (event) => {
    setSortBy(event.target.value);
  };
  return (
    <Box>
      <Announcement />
      <Navbar />
      <Container sx={{ py: 3 }}>
        <Grid container spacing={2}>
          <Grid item md={3} sm={6} xs={12}>
            <FormControl fullWidth size="small">
              <InputLabel id="filter-products-label">
                Filter Products:
              </InputLabel>
              <Select
                labelId="filter-products-label"
                id="filter-products"
                value={age}
                label="Filter Products"
                onChange={handleChange}
              >
                <MenuItem value={10}>Ten</MenuItem>
                <MenuItem value={20}>Twenty</MenuItem>
                <MenuItem value={30}>Thirty</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          <Grid item md={3} sm={6} xs={12}>
            <FormControl fullWidth size="small">
              <InputLabel id="Sort-products-label">Sort Products:</InputLabel>
              <Select
                labelId="sort-products-label"
                id="sort-products"
                value={sortBy}
                label="Sort Products"
                onChange={handleSortBy}
              >
                <MenuItem value={""}>None</MenuItem>
                <MenuItem value={10}>Popularity</MenuItem>
                <MenuItem value={20}>Latest</MenuItem>
                <MenuItem value={30}>Average rating</MenuItem>
                <MenuItem value={40}>Price: low to high</MenuItem>
                <MenuItem value={50}>Price: high to low</MenuItem>
              </Select>
            </FormControl>
          </Grid>
        </Grid>
      </Container>
      <Products />
      <Newsetter />
      <Footer />
    </Box>
  );
};

export default ProductList;
