import { useState } from "react";

const Task2 = () => {
  const [item, setItem] = useState([]);
  const addItem = () => {
    setItem((prevItem) => {
      console.log(prevItem);
      return prevItem.push(`<li>item ${0}</li>`);
    });
  };
  return (
    <>
      <button onClick={addItem}>Add</button>
      <ul>{item}</ul>
    </>
  );
};

export default Task2;
