import { ArrowLeftOutlined, ArrowRightOutlined } from "@mui/icons-material"
import { useState } from "react"
import styled from "styled-components"
import { SliderData } from "../data"

const Arrow = styled.button`
border: none;
width: 50px;
height: 50px;
border-radius: 50%;
background-color: teal;
display: grid;
place-content: center;
position: absolute;
top: 0;
bottom: 0;
margin: auto;
left: ${prop=> prop.direction === "left" && "10px"};
right: ${prop=> prop.direction === "right" && "10px"};
z-index: 2;
`
const Wrapper = styled.div`
  height: 800px;
  display: flex;
  transition: all 1.5s ease;
  transform: translateX(${props=>props.slideIndex * -100}vw);
  `
const Slide = styled.div`
position: relative;
  background-color: aliceblue;
  width: 100vw;
  height: 100%;
  overflow: hidden;
  display: flex;
`
const Slider = () => {
  const [slideIndex, setSlideIndex] =useState(0)
  const onChangeSlide = (direction)=> {
    setSlideIndex(direction)
    if(direction === 'left'){
      setSlideIndex(slideIndex > 0 ? slideIndex - 1: 2)
    } else{
      setSlideIndex(slideIndex < 2 ? slideIndex + 1: 0)
    }
  }
  return (
    <div className='slider'>
      <Arrow direction="left" onClick={()=> onChangeSlide('left')}>
        <ArrowLeftOutlined/>
      </Arrow>
      <Wrapper slideIndex={slideIndex}>
      {
        SliderData.map((element)=> (
      <Slide className="slide">
        <div className="slide-img">
          <img src={element.imgSrc} alt="E Tech World" />
        </div>
        <div className="slide-text">
          <p>EMilius</p>
        </div>

      </Slide>
        ))
      }
      </Wrapper>
      <Arrow direction="right"  onClick={()=> onChangeSlide('left')}>
        <ArrowRightOutlined/>
      </Arrow>
    </div>
  )
}

export default Slider
