import { useState } from "react";

const Task = (initialCount) => {
  const [count, setCount] = useState(initialCount);
  const add = () => {
    setCount(initialCount + 1);
  };
  const minus = () => {
    setCount(initialCount - 1);
  };
  const reset = () => {
    setCount(initialCount);
  };
  return (
    <div>
      <button onClick={add}> - </button>
      <h1>{reset}</h1>
      <button onClick={minus}> + </button>
    </div>
  );
};
export default Task;
